Parse.Cloud.define("SendSMS",function(request,response){
 //require the Twilio module and create a REST client

  var to = request.params['to']
  var text = request.params['text']
  var client = require('twilio')('AC232af210ccab2a4e50750cab4d61aa29','59935769ff3a31100ab889996dbce2e8');
 //Send an SMS text message
  client.sendMessage({
 // you can use request.params to pass the number you want to send
    to:to, // Any number Twilio can deliver to
    from: '+18189459557', // A number you bought from Twilio and can use for outbound communication
    body: text // body of the SMS message

  }, {
    function(error, responseData) {
      if (error) {
        // console.log(error);
        response.error(error);
      } else {
        // console.log(responseData);
        response.success(responseData); 
      }
    }
   }
  ); 
});
