package service.indexer;

import domain.entity.RetroIndex;
import domain.valueobject.Document;
import domain.valueobject.Word;
import org.jsoup.Jsoup;
import service.indexer.utils.Constants;

import java.io.IOException;
import java.net.URISyntaxException;
import java.net.URL;
import java.util.*;

/**
 * Created by hadri on 06/12/2017.
 */
public class Indexer implements IIndexer {

    private RetroIndex ri;

    public Indexer() {
        this.ri = new RetroIndex();
    }

    public ArrayList<Document> request(final String token) {
        ArrayList<String> words = CleanToken(token);
        for (String w: words) {
            for (String suff: Constants.SUFF_WORDS) {
                if (token.endsWith(suff)) {
                    w = w.substring(0, w.length() - suff.length());
                }
            }
        }
        ArrayList<Document> res = new ArrayList<Document>();
        ArrayList<Document> fullDocList = ri.getDocuments();

        for (String newToken: words) {
            ArrayList<Document> docs =  ri.getMap().get(newToken);
            TreeMap<Float, Document> tfidf = new TreeMap<Float, Document>(Collections.reverseOrder());
            float idf = (float) (Math.log10((float)fullDocList.size() / docs.size()) / Math.log10(2.));
            for (Document doc: docs) {
                for (Word w: doc.getWords()) {
                    if (w.getToken().equals(newToken)) {
                        tfidf.put(w.getFrequency() * idf, doc);
                    }
                }
            }

            for (Map.Entry<Float,Document> e : tfidf.entrySet()) {
                System.out.println(e.getKey());
                res.add(e.getValue());
            }
        }
        for (Document d: res) {
            System.out.println(d.getUrl());
        }
        return res;
    }

    private ArrayList<String> CleanToken(final String content) {
        ArrayList<String> words = new ArrayList<String>(Arrays.asList(content.replaceAll("[^a-zA-Z ]", "").toLowerCase().split("\\s+")));
        words.removeAll(Constants.STOP_WORDS);
        return words;
    }

    public void index(final URL url) {
        try {
            ArrayList<Word> ws = new ArrayList<Word>();
            org.jsoup.nodes.Document d = Jsoup.connect(url.toURI().toString()).get();
            String content = Jsoup.parse(d.outerHtml(),"ISO-8859-1").select("body").text();
            ArrayList<String> words = CleanToken(content);
            HashMap<String, ArrayList<Integer>> wMap = new HashMap<String, ArrayList<Integer>>();
            for (String w: words) {
                String wNew = w;
                for (String suff: Constants.SUFF_WORDS) {
                    if (w.endsWith(suff)) {
                        wNew = w.substring(0, w.length() - suff.length());
                    }
                }
                if (wMap.containsKey(wNew)) {
                    wMap.get(wNew).add(words.indexOf(w));
                } else {
                    wMap.put(wNew, new ArrayList<Integer>(Arrays.asList(words.indexOf(w))));
                }
                w = wNew;
            }
            for (Map.Entry<String,ArrayList<Integer>> e : wMap.entrySet()) {
                ws.add(new Word(e.getKey(), wMap.get(e.getKey()), (float)wMap.get(e.getKey()).size() / words.size()));
            }
            fillRetroIndex(new Document(url.toURI().toString(), ws));
        } catch (IOException e) {
            e.printStackTrace();
        } catch (URISyntaxException e) {
            e.printStackTrace();
        }
    }

    private void fillRetroIndex(Document doc) {
        System.out.println("save " + doc.getUrl());
        this.ri.getDocuments().add(doc);
        for (Word w: doc.getWords()) {
            if (this.ri.getMap().containsKey(w.getToken())) {
                this.ri.getMap().get(w.getToken()).add(doc);
            } else {
                this.ri.getMap().put(w.getToken(), new ArrayList<Document>(Arrays.asList(doc)));
            }
        }
    }
}
