function encodeForAjax(data) {
  return Object.keys(data).map(function(k){
    return encodeURIComponent(k) + '=' + encodeURIComponent(data[k])
  }).join('&');
}

Ext.define('CUX.uiue3.Callback3', {
    extend: 'Unidata.uiuserexit.callback.CallbackBase',

    requires: [
        'Unidata.uiuserexit.callback.CallbackTypes'
    ],

    statics: {
        active: true,

        isProvideable: function (opts) {
            return true;
        },

        callbackType: Unidata.uiuserexit.callback.CallbackTypes.DATAVIEWER_SHOW,

        // функция реализующая логику кастомера
        callback: function (params) {
            var dataViewer = params.dataViewer,
                dataRecord = dataViewer.getDataRecord(),
                metaRecord = dataViewer.getMetaRecord(),
                attributeName = 'id',
                dataAttribute,
                metaAttribute,
                dataAttribute;

            if (!dataRecord.phantom) {
                return;
            }

            if (Unidata.util.MetaRecord.isLookup(metaRecord)) {
                metaAttribute = Unidata.util.UPathMeta.findCodeAttributeByName(metaRecord, attributeName);
                dataAttribute = Unidata.util.UPathData.findFirstAttributeByPath(dataRecord, attributeName);

                if (metaAttribute && !dataAttribute) {
                    dataAttribute = Ext.create('Unidata.model.data.CodeAttribute', {
                        name: attributeName,
                        value: -1,
                        type: metaAttribute.get('typeValue')
                    });

                    dataRecord.codeAttributes().add(dataAttribute);
                }
            } else if (Unidata.util.MetaRecord.isEntity(metaRecord)) {
                metaAttribute = Unidata.util.UPathMeta.findSimpleAttributeByName(metaRecord, attributeName);
                dataAttribute = Unidata.util.UPathData.findFirstAttributeByPath(dataRecord, attributeName);

                if (metaAttribute && !dataAttribute) {
                    dataAttribute = Ext.create('Unidata.model.data.SimpleAttribute', {
                        name: attributeName,
                        value: -1,
                        type:  metaAttribute.get('typeValue'),
                        valueId: null,
                        unitId: null
                    });

                    dataRecord.simpleAttributes().add(dataAttribute);
                }
            }

            return;
        }
    }
