#define CRT_SECURE_NO_WARNINGS
#include <stdio.h>
#include <mpi.h>
#include <math.h>

#define A 8.2
#define B -0.7
#define C 0.64
#define D 0.18

double F(double x1, double x2)
{
	return A*x1 + B*x2 + exp(C*x1*x1 + D*x2*x2);
}

int main(int arc, char **argv) {

	int size, rank, msgtag = 12;
	MPI_Status status;

	if (MPI_Init(&arc, &argv) != MPI_SUCCESS) return 1;

	if (MPI_Comm_size(MPI_COMM_WORLD, &size) != MPI_SUCCESS) {

		MPI_Finalize();

		return 2;

	}

	if (MPI_Comm_rank(MPI_COMM_WORLD, &rank) != MPI_SUCCESS) {

		MPI_Finalize();

		return 3;

	}

	double xmax = 5.0, ymax = 5.0;
	double h1 = 0.1, h2 = 0.001, x = -xmax, y = -ymax;

	//поиск кол-ва квадратов по х и у
	double kor = sqrt((double)size);
	int divider = 1;
	int aX, aY;//кол-во блоков по х и у

	while (divider <= kor) {
		if (!(size % divider))
			aX = divider;
		divider++;
	}
	aY = size / aX;

	//Разбиение на квадраты
	double lxBlock = (xmax - x) / aX;//длина блока
	double lyBlock = (ymax - y) / aY;//высота блока

	x += (rank%aX)*lxBlock;//левый нижний
	y += (rank%aY)*lyBlock;//левый нижний

	int nx = (int)(lxBlock / h1 + 1);//кол-во точек по х
	int ny = (int)(lyBlock / h1 + 1);//кол-во точек по у

	double min[3];//0 - значение, 1- х, 2-у
	double minOther[3];//массивы с других процессов
	min[0] = F(x, y);

	for (int i = 0; i < nx; i++)
		for (int j = 0; j < ny; j++)
			if (F(x + i*h1, y + j*h1) < min[0]) {
				min[0] = F(x + i*h1, y + j*h1);
				min[1] = x + i*h1;
				min[2] = y + j*h1;
			}

	//поиск минимума
	int minRank;//на каком процессе минимум
	if (!rank)
	{
		for (int i = 1; i < size; i++)
		{
			MPI_Recv(&minOther, 3, MPI_DOUBLE, i, msgtag, MPI_COMM_WORLD, &status);
			if (min[0] > minOther[0])
			{
				for (int j = 0; j < 3; j++)
					min[j] = minOther[j];
				minRank = status.MPI_SOURCE;
			}
		}
		x = (minRank%aX)*lxBlock - xmax;//левый нижний
		y = (minRank%aY)*lyBlock - ymax;//левый нижний

		for (int i = 1; i < size; i++)
		{
			MPI_Send(&x, 1, MPI_DOUBLE, i, msgtag, MPI_COMM_WORLD);
			MPI_Send(&y, 1, MPI_DOUBLE, i, msgtag, MPI_COMM_WORLD);
		}
	}
	else
	{
		MPI_Send(min, 3, MPI_DOUBLE, 0, msgtag, MPI_COMM_WORLD);

		MPI_Recv(&x, 1, MPI_DOUBLE, 0, msgtag, MPI_COMM_WORLD, &status);
		MPI_Recv(&y, 1, MPI_DOUBLE, 0, msgtag, MPI_COMM_WORLD, &status);
	}

	//пересчитываем предыдущие значения
	lxBlock /= aX;
	lyBlock /= aY;

	x += (rank%aX)*lxBlock;
	y += (rank%aY)*lyBlock;

	nx = (int)(lxBlock / h2 + 1);
	ny = (int)(lyBlock / h2 + 1);

	//ищем более точные минимумы
	min[0] = F(x, y);

	for (int i = 0; i < nx; i++)
		for (int j = 0; j < ny; j++)
			if (F(x + i*h2, y + j*h2) < min[0]) {
				min[0] = F(x + i*h2, y + j*h2);
				min[1] = x + i*h2;
				min[2] = y + j*h2;
			}

	double globalMin = 0.0;
	MPI_Reduce(&min[0], &globalMin, 1, MPI_DOUBLE, MPI_MIN, 0, MPI_COMM_WORLD);

	FILE *f;
	//с помощью Reudce
	if (!rank)
	{
		f = fopen("outReduce.txt", "w");
		fprintf(f, "%f", globalMin);
		fclose(f);
	}

	//по старинке
	if (!rank)
	{
		for (int i = 1; i < size; i++)
		{
			MPI_Recv(&minOther, 3, MPI_DOUBLE, i, msgtag, MPI_COMM_WORLD, &status);
			if (min[0] > minOther[0])
			{
				for (int j = 0; j < 3; j++)
					min[j] = minOther[j];
				minRank = status.MPI_SOURCE;
			}
		}

		f = fopen("outOld.txt", "w");
		fprintf(f, "%f", min[0]);
		fclose(f);
	}
	else
	{
		MPI_Send(min, 3, MPI_DOUBLE, 0, msgtag, MPI_COMM_WORLD);
	}

	MPI_Finalize();
	return 0;
}
