def _ctx_run(steps):
    for step in steps:
        proc = _procs[step[0]]
        proc(step[1])

def _p_loop(p):
     for _ in range(p[0]):
         _ctx_run(p[1])

def _p_move(p):
     print("move")

def _p_turn(p):
     print("turn {}".format(p[0]))

_procs = {
