#include <sodium.h>
#include <iostream>
#include <string>

#include <chrono>
#include <random>

#include <future>
#include <thread>

namespace sodium_crypto {
    std::string bin2hex(const std::string& bytes) {
        std::string hex(bytes.size()*2+1, 0);
        sodium_bin2hex(&hex[0], hex.size(), reinterpret_cast<const unsigned char *>(&bytes[0]), bytes.size());

        return hex;
    }

    std::string sha256(std::string msg) {
        unsigned char out[crypto_hash_sha256_BYTES];
        crypto_hash_sha256(out, reinterpret_cast<const unsigned char *>(msg.c_str()), msg.length());
        return std::string(reinterpret_cast<char *>(out), crypto_hash_sha256_BYTES);
    }
}

struct numbers {
    int n1;
    int n2;
    int n3;
    std::string to_string() {
        return std::to_string(n1) + "," + std::to_string(n2) + "," + std::to_string(n3);
    }
};

// returns found numbers
void find_next_hash(int steps, int size, std::vector<numbers> previous_nums, std::string previous_hash, std::string hash_to_find) {
    for(int i = 0; i < size; ++i)
        for(int j = 0; j < size; ++j)
            for(int k = 0; k < size; ++k) {
                previous_nums.push_back({i,j,k});
                std::string hash = sodium_crypto::sha256(previous_hash + previous_nums.back().to_string());
                // std::cout << "find_next_hash: " << sodium_crypto::bin2hex(hash) << "\n";
                if (steps > 1) {
                    find_next_hash(steps-1, size, previous_nums, hash, hash_to_find);
                }
                else if (hash == hash_to_find) {
                    std::cout << "Found! " << sodium_crypto::bin2hex(hash) << "\n";
                    std::cout << "Numbers:\n";
                    for(auto n : previous_nums) {
                        std::cout << n.to_string() << '\n';
                    }
                }
                previous_nums.pop_back();
            }
}

// returns found numbers
void find_hash(int steps, int size, std::string hash_to_find) {
    std::vector<numbers> nums;
    std::string empty_hash;
    for(int i = 0; i < size; ++i)
        for(int j = 0; j < size; ++j)
            for(int k = 0; k < size; ++k) {
                nums.push_back({i,j,k});
                std::string hash = sodium_crypto::sha256(empty_hash + nums.back().to_string());
                // std::cout << "find_hash: " << sodium_crypto::bin2hex(hash) << "\n";
                if (steps > 1) {
                    // std::async(std::launch::async, find_next_hash, steps-1, size, hash, hash_to_find);
                    std::thread t(find_next_hash, steps-1, size, nums, hash, hash_to_find); t.join();
                    // find_next_hash(steps-1, size, hash, hash_to_find);
                }
                else if (hash == hash_to_find) {
                    std::cout << "Found! " << sodium_crypto::bin2hex(hash) << "\n";
                    std::cout << "Numbers: " << nums.at(0).to_string() << '\n';
                }
                nums.pop_back();
            }
}

// with n steps and size
std::string generate_problem(int steps, int size) {
    std::random_device rd;  //Will be used to obtain a seed for the random number engine
    std::mt19937 gen(rd()); //Standard mersenne_twister_engine seeded with rd()
    std::uniform_int_distribution<> dis(0, size-1);
    std::cout << "\nGenerating new problem!:" << '\n';

    std::string hash_to_guess;
    for(int i = 0; i < steps; ++i) {
        numbers nums;
        nums.n1 = dis(gen);
        nums.n2 = dis(gen);
        nums.n3 = dis(gen);

        // std::cout << "previous hash: " << sodium_crypto::bin2hex(hash_to_guess) << '\n';
        std::cout << "adding numbers to hash: " << nums.to_string() << '\n';

        hash_to_guess = sodium_crypto::sha256(hash_to_guess + nums.to_string());

        // std::cout << "step[" << i << "] sha256 hash to guess: " << sodium_crypto::bin2hex(hash_to_guess) << '\n';
    }
    return hash_to_guess;
}

int main() {

    size_t problem_size = 4;
    for(int step = 1; step < 5; ++step) {
        std::string problem = generate_problem(step, problem_size);
        std::cout << "steps: " << step << ", problem_size: " << problem_size << '\n';
        std::cout << "start bruteforce... (to find [" << sodium_crypto::bin2hex(problem) << "])\n";
        auto start = std::chrono::steady_clock::now();

        find_hash(step, problem_size, problem);

        auto end = std::chrono::steady_clock::now();
        std::chrono::duration<double> elapsed_seconds = end - start;
        std::cout << "elapsed time: " << elapsed_seconds.count() << "s\n";
    }
    return 0;
}

