var Agent = Ext.data.Record.create([
    {
        name: 'id',
        type: 'string'
    },
    {
        name: 'login',
        type: 'string'
    }
]);

var proxyAgents = new Ext.data.HttpProxy({
    url: 'store/agents.php'
});

var readerAgents = new Ext.data.JsonReader(
{
    idProperty: "id",
    root: "rows",
    totalProperty: "results",
    successProperty: "success"
}, Agent);

var writerAgents = new Ext.data.JsonWriter({
    encode: true,
    writeAllFields: false
});

var storeAgents = new Ext.data.GroupingStore({
    writer: writerAgents,
    reader: readerAgents,
    proxy: proxyAgents,
    sortInfo: {field: 'login', direction: 'ASC'}
}, Agent);

storeAgents.load();

#include <stdio.h>
#include <cs50.h>

float no(float change);
int a = 0;
int b = 0;
int c = 0;
int d = 0;

int main(void)
{
    printf("O hai! How much change is owed?\n");
    float x = get_float();
    no(x);
    printf("%i", a + b + c + d);
}

float no(float change)
{
    do
    {
        switch(change)
        {
            case change > 0.25:
                change = change - 0.25;
                a++;
                break;
            case change > 0.10:
                change = change - 0.10;
                b++;
                break;
            case change > 0.05:
                change = change - 0.05;
                c++;
                break;
            case change > 0.01:
                change = change - 0.01;
                d++;
                break;
        }
    }
    while(change > 0);
}
