const int frameHeight = 640;
const int frameWidth = 480;

const int frameSize = frameWidth * frameHeight;

int rgb[frameSize];

// convert from YUV420 to RGB
extern "C" void yuv420toRGB(jbyte * data)
{
	for (int j = 0, yp = 0; j < frameHeight; j++)
	{
		int uvp = frameSize + (j >> 1) * frameWidth, u = 0, v = 0;

		for (int i = 0; i < frameWidth; i++, yp++)
		{
			int y = (0xff & ((int) data[yp])) - 16;
			if (y < 0) y = 0;
			if ((i & 1) == 0)
			{
				v = (0xff & data[uvp++]) - 128;
				u = (0xff & data[uvp++]) - 128;
			}

			int y1192 = 1192 * y;
			int r = (y1192 + 1634 * v);
			int g = (y1192 - 833 * v - 400 * u);
			int b = (y1192 + 2066 * u);

			if (r < 0) r = 0; else if (r > 262143) r = 262143;
			if (g < 0) g = 0; else if (g > 262143) g = 262143;
			if (b < 0) b = 0; else if (b > 262143) b = 262143;

			rgb[yp] = 0xff000000 | ((r << 6) & 0xff0000) | ((g >> 2) & 0xff00) | ((b >> 10) & 0xff);
		}
	}
}

public class CoVal
{
	public int x;
	public int y;
	public int value;
}

public int sumOfNeighbours(CoVal current, List<CoVal> all)
{
	var a = all.SingleOrDefault(x => x.x == current.x && x.y == (current.y - 1))?.value;
	var b = all.SingleOrDefault(x => x.x == current.x && x.y == (current.y + 1))?.value;
	var c = all.SingleOrDefault(x => x.y == current.y && x.x == (current.x - 1))?.value;
	var d = all.SingleOrDefault(x => x.y == current.y && x.x == (current.x + 1))?.value;

	var e = all.SingleOrDefault(x => x.x == current.x - 1 && x.y == (current.y + 1))?.value;
	var f = all.SingleOrDefault(x => x.x == current.x + 1 && x.y == (current.y + 1))?.value;
	var g = all.SingleOrDefault(x => x.x == current.x - 1 && x.y == (current.y - 1))?.value;
	var h = all.SingleOrDefault(x => x.x == current.x + 1 && x.y == (current.y - 1))?.value;

	return (a ?? 0) + 
		   (b ?? 0) + 
		   (c ?? 0) + 
		   (d ?? 0) + 
		   (e ?? 0) + 
		   (f ?? 0) + 
		   (g ?? 0) + 
		   (h ?? 0);
}

void Main()
{
	var endSpace = 368078;
	var result = 0;
	var visited = new List<CoVal>{
					new CoVal{ x=0, y=0, value=1}
				};

	// how far have we gone?
	var sum = 1;
	// start at the origin
	var x = 0;
	var y = 0;

	// move one space at the beginning
	var n = 1;
	var keepGoing = true;
	while(keepGoing){
		keepGoing = ruld(ref x,ref y,ref n, ref sum, ref visited, endSpace, ref result);
	}

	result.Dump();
}

private bool checkIfFinished(int endspace, int nextValue){
	if(nextValue > endspace)
		return true;

	return false;
}

private int remainingDistance(int endSpace, int distance){
	return endSpace - distance;
}

private bool ruld(ref int x, ref int y, ref int n, ref int count, ref List<CoVal> visited, int endspace, ref int result)
{
	//right
	for(int i = 0; i<n;i++){
		x++;
		var newOne = new CoVal{ x=x, y=y };
		var sum = sumOfNeighbours(newOne, visited);
		if(sum > endspace){
			result = sum;
			return false;
		}
		newOne.value = sum;
		count += sum;
		visited.Add(newOne);
	}	

	//up
	for (int i = 0; i < n; i++)
	{
		y++;
		var newOne = new CoVal { x = x, y = y };
		var sum = sumOfNeighbours(newOne, visited);
		if (sum > endspace)
		{
			result = sum;
			return false;
		}
		newOne.value = sum;
		count += sum;
		visited.Add(newOne);
	}

	// now increase n
	n++;

	//left	
	for (int i = 0; i < n; i++)
	{
		x--;
		var newOne = new CoVal { x = x, y = y };
		var sum = sumOfNeighbours(newOne, visited);
		if (sum > endspace)
		{
			result = sum;
			return false;
		}
		newOne.value = sum;
		count += sum;
		visited.Add(newOne);
	}

	//down
	for (int i = 0; i < n; i++)
	{
		y--;
		var newOne = new CoVal { x = x, y = y };
		var sum = sumOfNeighbours(newOne, visited);
		if (sum > endspace)
		{
			result = sum;
			return false;
		}
		newOne.value = sum;
		count += sum;
		visited.Add(newOne);
	}


	n++;

	return true;
}

