#include <iostream>
#include <vector>

double solve(int n_, int h_) {
    if (n_ + 1 < h_) {
        return 0.0;
    }
    if (h_ == 0 && n_ > 1) {
        return 0.0;
    }
    if (h_ == 0 && n_ < 2) {
        return 1.0;
    }
    
    std::vector<std::vector<double>> proba; // вероятность, что H > h_
    
    proba.resize(n_ + 1);
    for (auto& i : proba) {
        i.resize(h_ + 1);
    }
    for (int i = 2; i < n_ + 1; ++i) {
        for (int j = 0; j < h_ + 1; ++j) {
            proba[i][j] = 1; // = 0;
        }
    }
    
    for (int i = 1; i < h_ + 1; ++i) { // цикл по высотам
        for (int j = 2; j < n_ + 1; ++j) { // цикл по размерам дучи
            double cur_sum = 0;
            for (int k = 0; k < j; ++k) { // цикл по размерам дучи
                cur_sum = cur_sum + proba[k][i - 1] + proba[j - k - 1][i - 1]
                                - proba[k][i - 1] * proba[j - k - 1][i - 1];
            }
            proba[j][i] = cur_sum / j;
        }
    }
    return proba[n_][h_ - 1] - proba[n_][h_]; // вероятность, что равно
    // return proba[n_][h_];
}

int main(int argc, const char * argv[]) {
    std::ios_base::sync_with_stdio(false);
    std::cin.tie(nullptr);
    std::cout.precision(10);
    int n_, h_;
    std::cin >> n_ >> h_;
    std::cout << solve(n_, h_) << std::endl;
    return 0;
}

