    public class Item<T>
    {
        [NotMapped]
        public PlatformInsuranceContext Context { get; set; }

        public List<T> GetList()
        {
            List<T> data = Context.Item.ToList();

            return data;
        }

        public T GetByRef(Guid refReq)
        {
            Context.Item<T>.FirstOrDefault(x => x.Ref == refReq);
        }
	}


public class Company : Item<Company>
    {
        [Key, DatabaseGenerated(DatabaseGeneratedOption.Identity), Required]
        public Guid Ref { get; set; }
        [MaxLength(9), DatabaseGenerated(DatabaseGeneratedOption.Identity), Required]
        public String Code { get; set; }
        [MaxLength(150), Required]
        public String Name { get; set; }
        [MaxLength(250)]
        public String FullName { get; set; }
        public Boolean MarkDelete { get; set; }
    }

