public Form1()
        {
            List<Team[]> allpermutations = new List<Team[]>();
            List<Team[]> legalpermutations = new List<Team[]>();

            Team[] winarray = new Team[8];
            winarray[0] = new Team("Man Utd", "ENG", 'A');
            winarray[1] = new Team("PSG", "FRA", 'B');
            winarray[2] = new Team("Roma", "ITA", 'C');
            winarray[3] = new Team("Barcelona", "ESP", 'D');
            winarray[4] = new Team("Liverpool", "ENG", 'E');
            winarray[5] = new Team("Man City", "ENG", 'F');
            winarray[6] = new Team("Besiktas", "TUR", 'G');
            winarray[7] = new Team("Tottenham", "ENG", 'H');
            Team[] ruarray = new Team[8];
            ruarray[0] = new Team("Basel", "SWI", 'A');
            ruarray[1] = new Team("Bayern", "GER", 'B');
            ruarray[2] = new Team("Chelsea", "ENG", 'C');
            ruarray[3] = new Team("Juventus", "ITA", 'D');
            ruarray[4] = new Team("Sevilla", "ESP", 'E');
            ruarray[5] = new Team("Shakhtar", "UKR", 'F');
            ruarray[6] = new Team("Porto", "POR", 'G');
            ruarray[7] = new Team("Real Madrid", "ESP", 'H');

            bool valid = false;
            allpermutations = new PermutationFinder<Team>().Evaluate(ruarray, Evaluate);
            foreach (var item in allpermutations)
            {
                for (int j = 0; j < 8; j++)
                {
                    if (item[j].getCountry() == winarray[j].getCountry() || item[j].getGroup() == winarray[j].getGroup())
                    {
                        valid = false;
                        break;
                    }
                    else
                        valid = true;
                }
                if (valid == true)
                    legalpermutations.Add(item);
            }
            float[] ia = new float[8]; float[] ib = new float[8]; float[] ic = new float[8]; float[] id = new float[8];
            float[] ie = new float[8]; float[] ig = new float[8]; float[] ih = new float[8]; float[] ii = new float[8];
            List<float[]> lint = new List<float[]>();

            for (int j = 0; j < ia.Length; j++)
            {
                ia[j] = 0; ib[j] = 0; ic[j] = 0; id[j] = 0; ie[j] = 0; ig[j] = 0; ih[j] = 0; ii[j] = 0;
            }
            lint.Add(ia); lint.Add(ib); lint.Add(ic); lint.Add(id); lint.Add(ie); lint.Add(ig); lint.Add(ih); lint.Add(ii);
            for (int i = 0; i < legalpermutations.Count; i++)
                for (int j = 0; j < legalpermutations[i].Length; j++)
                {
                    if (legalpermutations[i][j] == ruarray[0]) lint[j][0] += 1;
                    else if (legalpermutations[i][j] == ruarray[1]) lint[j][1] += 1;
                    else if (legalpermutations[i][j] == ruarray[2]) lint[j][2] += 1;
                    else if (legalpermutations[i][j] == ruarray[3]) lint[j][3] += 1;
                    else if (legalpermutations[i][j] == ruarray[4]) lint[j][4] += 1;
                    else if (legalpermutations[i][j] == ruarray[5]) lint[j][5] += 1;
                    else if (legalpermutations[i][j] == ruarray[6]) lint[j][6] += 1;
                    else if (legalpermutations[i][j] == ruarray[7]) lint[j][7] += 1;
                }
            float tmp;
            for (int i = 0; i < 8; i++)
                for (int j = 0; j < 8; j++)
                {
                    tmp = Convert.ToSingle(lint[i][j] / legalpermutations.Count);
                    //tmp = Convert.ToSingle(Math.Round(tmp, 4));
                    tmp = tmp * 100;
                    lint[i][j] = tmp;
                }
            Console.Write('\t');
            for (int i = 0; i < ruarray.Count(); i++)
                Console.Write(ruarray[i].getTeam() + " ");
            Console.Write('\n');
            for (int i = 0; i < lint.Count; i++)
                Console.Write(winarray[i].getTeam() + "      " + lint[i][0] + " " + lint[i][1] + " " + lint[i][2] + " " + lint[i][3] + " " + lint[i][4] + " " + lint[i][5] + " " + lint[i][6] + " " + lint[i][7] + '\n');
        }
