#include <iostream>
#include <vector>
#include <deque>
using namespace std;
void out(int a) {
	cout << a << " ";
}

int main() {
	int n;
	//cin >> n;


	//vector<int> v(n);
	/*for (int i = 0; i < n; ++i) {
		cin >> v[i];
	}
	*/
	n = 6;
	int v[] = { 4,5,9,8,6,7};
	deque<int> d;
	for (int i = 0; i < n; ++i) {
		d.push_back(v[i]);
	}
	int front, back, middleIndex, middle;
	while (!d.empty()) {
		front = d.front();
		out(front);
		if (d.empty())
			return 0;
		back = d.back();
		out(back);
		if (d.empty())
			return 0;
		middleIndex = (d.size() - 1) / 2;
		middle = d.at(middleIndex);
		out(middle);
		d.erase(d.begin() + middleIndex);
		if (d.empty())
			return 0;
		d.pop_front();
		d.pop_back();
	}
	

	return 0;
}

package br.ucb.modelo.dao;

import java.sql.Connection;
import java.sql.DriverManager;
import java.sql.SQLException;

public class ConnectionFactory {
	private static Connection con=null;

	public static Connection getConnection() throws SQLException {
		if (con == null) {
			try {
				Class.forName("com.mysql.jdbc.Driver");
				String sql = "jdbc:mysql://localhost/escola";
				con = DriverManager.getConnection(sql, "root", "root");
			}
			catch (ClassNotFoundException e) {
				throw new SQLException("Driver não localizado");
			}
		}
		return con;
	}

	public void finalize() {
		try {
			con.close();
		} catch (SQLException e) {
			System.out.println("Erro de fechamento do banco");
		}
	}
}
