#include <stdio.h>
#include <stdlib.h>
#include <locale.h>
 
#define DOUBLE_SPACE 1
 
typedef unsigned char cell;
enum { U = 1, D = 2, L = 4, R = 8, V = 16 };
 
wchar_t glyph[] =
	L"　│││─┘┐┤─└┌├─┴┬┼　┆┆┆┄╯╮ ┄╰╭ ┄";
 
int irand(int n)
{
	int r, rand_max = RAND_MAX - (RAND_MAX % n);
	while ((r = rand()) >= rand_max);
	return r / (rand_max/n);
}
 
#define FOR(i, h) for(i = 0; i <= h; i++)
cell **grid(int w, int h)
{
	int i, j;
	cell **x = malloc(sizeof(cell*) * h + h * w);
	x[0] = (cell*)(x + h);
	FOR(i, h-1) x[i+1] = x[i] + w;
	FOR(i, h-1) FOR(j, w-1) x[i][j] = 0;
	return x;
}
 
int rand_neighbor(cell **c, int w, int h, int x, int y, int *x2, int *y2) {
	int d, ofx[] = {1, -1, 0, 0}, ofy[] = {0, 0, -1, 1};
	int i, j, cnt = 0;
	FOR(d, 3) {
		j = x + ofx[d], i = y + ofy[d];
		if (j < 0 || j >= w || i < 0 || i >= h || (c[i][j] & V))
			continue;
		if (!irand(++cnt)) *x2 = j, *y2 = i;
	}
	return cnt;
}
 
int build(cell **c, int w, int h, int x, int y, int avail) {
	int x2, y2;
	c[y][x] |= V;
 
	while (avail) {
		if (!rand_neighbor(c, w, h, x, y, &x2, &y2)) break;
		if (x2 > x) c[y][x] |= R, c[y][x2] |= L; else
		if (x2 < x) c[y][x] |= L, c[y][x2] |= R; else
		if (y2 > y) c[y][x] |= D, c[y2][x] |= U; else
		if (y2 < y) c[y][x] |= U, c[y2][x] |= D;
 
		avail = build(c, w, h, x2, y2, avail - 1);
	}
 
	return avail;
}
 
cell** make_maze(int w, int h)
{
	cell **cells = grid(w, h);
	build(cells, w, h, 0, 0, w * h - 1);
	return cells;
}
 
int walk(cell **c, cell **s, int w, int h, int x, int y, int x2, int y2)
{
	int i, j, d, dirs[4][2] = {{0, -1}, {0, 1}, {-1, 0}, {1, 0}};
 
	s[y][x] = V;
	if (y == y2 && x == x2) return 1;
 
	FOR(d, 3) {
		if (!(c[y][x] & (1 << d))) continue;
		i = y + dirs[d][1];
		j = x + dirs[d][0];
 
		if (i < 0 || i >= h || j < 0 || j >= w) continue;
		if (s[i][j] & V) continue;
 
		if (walk(c, s, w, h, j, i, x2, y2)) break;
	}
 
	if (d <= 3) {
		s[y][x] |= (1 << d);
		if (d <= 1) s[i][j] |= 1 << (1 - d);
		else s[i][j] |= 1 << (5 - d);
		return 1;
	}
	return 0;
}
 
void show_maze(cell **c, cell **s, int w, int h)
{
	int i, j, x, y;
	cell **tile = grid(2*w + 1, 2*h + 1);
 
	FOR(i, 2*h) FOR(j, 2*w)
		tile[i][j] = ((j&1) ? 0 : (U|D)) | ((i&1) ? 0 : (L|R));
 
	FOR(i, 2*h) tile[i][0] &= ~L, tile[i][2*w] &= ~R;
	FOR(j, 2*w) tile[0][j] &= ~U, tile[2*h][j] &= ~D;
 
	FOR(i, h-1) FOR(j, w-1) {
		y = 2 * i + 1, x = 2 * j + 1;
 
		if (c[i][j] & R) {
			tile[y-1][x+1] &= ~D;
			tile[ y ][x+1] = 0;
			tile[y+1][x+1] &= ~U;
		}
		if (c[i][j] & D) {
			tile[y+1][x-1] &= ~R;
			tile[y+1][ x ] = 0;
			tile[y+1][x+1] &= ~L;
		}
		if(!s) continue;
 
		tile[y][x] = s[i][j];
		if (s[i][j] & U) tile[y-1][ x ] |= V|D;
		if (s[i][j] & D) tile[y+1][ x ] |= V|U;
		if (s[i][j] & L) tile[ y ][x-1] |= V|R;
		if (s[i][j] & R) tile[ y ][x+1] |= V|L;
	}
 
	FOR(i, 2*h) {
		FOR(j, 2*w)
			printf((tile[i][j] & V) ? "\033[31m%lc\033[m" : "%lc",
				glyph[ tile[i][j] ]);
		putchar('\n');
	}
	free(tile);
}
 
int main(int c, char *v[])
{
	cell **solu, **maze;
	int w = 0, h = 0;
 
	setlocale(LC_CTYPE, "");
	if (!DOUBLE_SPACE) glyph[0] = glyph[16] = L' ';
 
	if (c > 2) w = atoi(v[1]), h = atoi(v[2]);
	if (w <= 0) w = 16;
	if (h <= 0) h = 8;
 
	maze = make_maze(w, h);
	solu = grid(w, h);
	walk(maze, solu, w, h, 0, 0, w-1, h-1);
	show_maze(maze, solu, w, h);
	/* free(maze); free(solu); */
	return 0;
}
