OUTPUT_FILE = 'output.html'
DATA_BASE_FILE = 'results.json'
API_KEY = "AIzaSyDX9YBVra6xWazImXYtjQhiepmOKxiHl4Y"
ALARM_PATH = "resources//alarm.mp4"

def placeAllMarkers(vals):
	"""	makes a marker foreach help request
	
	@@param vals - a list containing all the marker requests represented inside a dictionary
	"""
	
	risks = {'1' : 'risk1.png', '2' : 'risk2.png', '3' : 'risk3.jpg'}
	
	print 'risk_level' in vals
	
	with open(OUTPUT_FILE, 'a') as f:
		for marker in vals:
			if not 'risk_level' in marker.keys():
				marker['risk_level'] = '1'
			elif not marker['risk_level'] in ['1', '2', '3']:
				marker['risk_level'] = '1'
		
			# creates a new marker
			f.write('addMarker({{position:{{lat: {0}, lng: {1}}}, icon:"{4}", content:"<h1>{2}</h1><h3>{3}</h3>"}});'.format(marker['lat'], marker['lng'], marker['content'], marker['time'], risks[marker['risk_level']]))
		
	with open(DATA_BASE_FILE, 'w') as f:
		json.dump(vals, f)
		
def play_sound():
	# plays the alarm
	playsound.playsound('alarm.mp3', True)

def create_map(current_marker):
	thread = Thread(target=play_sound)
	thread.start()
	
	vals = None
	
	if not os.path.exists(DATA_BASE_FILE):
		with open(DATA_BASE_FILE) as f:
			pass
	# reads all the previous markers
	with open(DATA_BASE_FILE, 'r') as f:
		try:
			vals = json.load(f)
		except:
			vals = []
		
	with open(OUTPUT_FILE, 'w') as f:
		f.write("""<html>
	<head>
	</head>
	
	<body>
		<div id='map', style='height:600px; width:100%'></div>
		
		<script>	
		
			var map;
		
			
			function addMarker(properties) 
			{{
				var marker = new google.maps.Marker({{
				position:properties.position,
				map:map
				}});
				
				if (properties.icon)
				{{
					marker.setIcon(properties.icon);
				}}
				
				if(properties.content)
				{{
					var infowindow = new google.maps.InfoWindow({{content:properties.content}});
					marker.addListener('click', function(){{infowindow.open(map, marker);}});
				}}
			}}
			
		
			function initMap() 
			{{			
				map = new google.maps.Map(document.getElementById('map'), {{
					center: {{lat: {0}, lng: {1}}},
					zoom: 14
				}}); """.format(current_marker['lat'], current_marker['lng']))
	
	placeAllMarkers([current_marker] + vals)
	
	with open(OUTPUT_FILE, 'a') as f:
		f.write("""
			}}
		</script>
		
		<script async defer
		src='https://maps.googleapis.com/maps/api/js?key={0}&callback=initMap'
		></script>
	</body>
</html>""".format(API_KEY))
	

class S(BaseHTTPRequestHandler):
    def _set_headers(self):
        self.send_response(200)
        self.send_header('Content-type', 'text/html')
        self.end_headers()

    def do_GET(self):
		# continues only if the user posts an help request
		if 'gethelp' in self.path:
			
			path = "someurl.org" + self.path
			parsed = urlparse.urlparse(path)
			# gets all the 
			vals = urlparse.parse_qs(parsed.query)
			
			if not 'risk_level' in vals.keys():
				vals['risk_level'] = '1'
			elif not vals['risk_level'] in ['1', '2', '3']:
				vals['risk_level'] = '1'
			
			
			if 'lat' in vals.keys() and 'lng' in vals.keys() and 'content' in vals.keys() and 'time' in vals.keys():
				create_map({key : value[0] for key, value in vals.items()})

			
		self._set_headers()
		self.wfile.write("<html><body><h1>hi!</h1></body></html>")

    def do_HEAD(self):
        self._set_headers()
        
    def do_POST(self):
        # Doesn't do anything with posted data
        self._set_headers()
        self.wfile.write("<html><body><h1>POST!</h1></body></html>")
        
def run(server_class=HTTPServer, handler_class=S, port=80):
	server_address = ('', port)
	httpd = server_class(server_address, handler_class)
	print server_address
	print 'Starting httpd...'
	httpd.serve_forever()

if __name__ == "__main__":
    from sys import argv

    if len(argv) == 2:
        run(port=int(argv[1]))
    else:
        run()

using System;
using System.Collections.Generic;
using C5;

namespace ArcanumMMOCommon
{
    public abstract class Cache<TKey, TValue>
    {
        private readonly TreeDictionary<Wrapper, TValue> _dict = new TreeDictionary<Wrapper, TValue>(new WrapperComparator<TKey>());
        private readonly uint _maxElements;
        private readonly Dictionary<TKey, Wrapper> _wrapDict = new Dictionary<TKey, Wrapper>();
        private ulong _counter;

        public abstract TValue GenerateValue(TKey key);

        public Cache(UInt32 maxElements)
        {
            _maxElements = maxElements;
        }

        public TValue this[TKey key]
        {
            get
            {
                if (!_wrapDict.ContainsKey(key))
                {
                    var w = new Wrapper(++_counter, key);
                    var v = GenerateValue(key);
                    if (_counter == 0)
                    {
                        _dict.Clear();
                        _wrapDict.Clear();
                    }
                    _dict.Add(w, v);
                    _wrapDict.Add(key, w);
                    return v;
                }
                Wrapper wrapper = _wrapDict[key];
                TValue value;
                if (!_dict.Remove(wrapper, out value))
                    throw new ApplicationException();
                wrapper.Counter = ++_counter;
                if (wrapper.Counter == 0)
                {
                    _dict.Clear();
                    _wrapDict.Clear();
                }
                else
                {
                    _dict.Add(wrapper, value);
                }
                return value;
            }
            set
            {
                if (_wrapDict.ContainsKey(key))
                {
                    Wrapper wrapper = _wrapDict[key];
                    if (!_dict.Remove(wrapper))
                        throw new ApplicationException();
                    wrapper.Counter = ++_counter;
                    if (wrapper.Counter == 0)
                    {
                        _dict.Clear();
                        _wrapDict.Clear();
                    }
                    else
                    {
                        if (!_wrapDict.Remove(key))
                            throw new ApplicationException();
                    }
                    _wrapDict.Add(key, wrapper);
                    _dict.Add(wrapper, value);
                }
                else
                {
                    if (_maxElements == _dict.Count)
                    {
                        var kvp = _dict.DeleteMin();
                        if (!_wrapDict.Remove(kvp.Key.Value))
                            throw new ApplicationException();
                    }
                    var wrapper = new Wrapper(++_counter, key);
                    if (_counter == 0)
                    {
                        _wrapDict.Clear();
                        _dict.Clear();
                    }
                    _wrapDict.Add(key, wrapper);
                    _dict.Add(wrapper, value);
                }
            }
        }

        #region Nested type: Wrapper

        private class Wrapper
        {
            public readonly TKey Value;
            public ulong Counter;

            public Wrapper(ulong counter, TKey value)
            {
                Counter = counter;
                Value = value;
            }
        }

        #endregion

        #region Nested type: WrapperComparator

        private class WrapperComparator<T> : IComparer<Wrapper>
        {
            #region IComparer<Cache<TKey,TValue>.Wrapper<TKey>> Members

            public int Compare(Wrapper x, Wrapper y)
            {
                ulong a = x.Counter;
                ulong b = y.Counter;
                if (a == b)
                {
                    return 0;
                }
                if (a > b)
                {
                    return 1;
                }
                return -1;
            }

            #endregion
        }

        #endregion
    }
}
