double calculateCost(double *matrix, const int *clusters, double *centroids, int size, int dim) {
    double totalCost = 0;

    for(int i = 0; i < size; ++i) {
        totalCost += euclideanDistance(&matrix[i * dim], &centroids[clusters[i] * dim], dim);
    }

    return totalCost;
}

