#include <iostream>

using namespace std;

int f(int n, int m)
{
	if(m==0)
		return n;
	else if(n==0)
		return m;
	else return f(n-1,m)+f(n,m-1)+f(n-1,m-1);
}

int main(int argc, char** argv) {
	
	int q;
	int n,m;
	
	cin>>q;
	
	for(int i=0;i<q;i++)
	{
		cin>>n>>m;
		cout<<f(n,m)<<endl;
	}
	
	return 0;
}
