public class CampionatFotbal {

	static Scanner scan = new Scanner(System.in);
	static ArrayList<String> array = new ArrayList<String>();

	public static void main(String[] args) {

		int k = scan.nextInt(); // nr echipe
		int n = scan.nextInt(); // nr meciuri
		int nrmeci = 0; // numarul meciului ca sa innumere cate array-uri citeste

		ArrayList<String> EchipaAcasa = new ArrayList<String>();
		ArrayList<Integer> GoluriAcasa = new ArrayList<Integer>();
		ArrayList<String> EchipaDeplasare = new ArrayList<String>();
		ArrayList<Integer> GoluriDeplasare = new ArrayList<Integer>();

		ArrayList<String> Echipa = new ArrayList<>();
		Echipa.add(EchipaAcasa.get(n));
		Echipa.add(EchipaDeplasare.get(n));

		String meciuri = scan.nextLine();
		for (int i = 0; i < n; i++) {
			array.add(meciuri);
			meciuri = scan.nextLine();
			EchipaAcasa.add(meciuri.split(" ")[0]);
			GoluriAcasa.add(Integer.valueOf(meciuri.split(" ")[1]));
			GoluriDeplasare.add(Integer.valueOf(meciuri.split(" ")[3]));
			EchipaDeplasare.add(meciuri.split(" ")[4]);

		}

		ArrayList<Integer> GoluriDateFinal = new ArrayList<Integer>();
		ArrayList<Integer> GoluriPrimiteFinal = new ArrayList<Integer>();
		ArrayList<Integer> PuncteEchipaFinal = new ArrayList<Integer>();

		for (Iterator<String> it = Echipa.iterator(); it.hasNext();) {
			String mEchipa = it.next();
			int puncteEchipa = 0;
			int goluriDate = 0;
			int goluriPrimite = 0;
			for (int j = 0; j < n; j++) {
				if (mEchipa.equals(EchipaAcasa.get(j))) {

					goluriDate += GoluriAcasa.get(j);
					goluriPrimite += GoluriDeplasare.get(j);

					if (GoluriAcasa.get(j) > GoluriDeplasare.get(j))
						puncteEchipa = puncteEchipa + 3;
					else if (GoluriAcasa.get(j) == GoluriDeplasare.get(j))
						puncteEchipa++;
				}

				else if (mEchipa.equals(EchipaDeplasare.get(j))) {

					goluriDate += GoluriDeplasare.get(j);
					goluriPrimite += GoluriAcasa.get(j);

					if (GoluriAcasa.get(j) < GoluriDeplasare.get(j))
						puncteEchipa = puncteEchipa + 3;
					else if (GoluriAcasa.get(j) == GoluriDeplasare.get(j))
						puncteEchipa++;
				}
			}
			
			GoluriDateFinal.add(goluriDate);
			GoluriPrimiteFinal.add(goluriPrimite);
			PuncteEchipaFinal.add(puncteEchipa);

		}
		
		for ( int i = 0; i < PuncteEchipaFinal.size() - 1; i++ ) {
			if ( PuncteEchipaFinal.get(i).compareTo(PuncteEchipaFinal.get(i+1)) > 0 ) {
				int aux = PuncteEchipaFinal.get(i);
				PuncteEchipaFinal.set(i, PuncteEchipaFinal.get(i+1));
				PuncteEchipaFinal.set(i+1, aux);
				
				String aux2 = (Echipa.get(i));
				Echipa.set(i, Echipa.get(i+1));
				Echipa.set(i+1, aux2); 
				
				aux = GoluriDateFinal.get(i);
				GoluriDateFinal.set(i, GoluriDateFinal.get(i+1));
				GoluriDateFinal.set(i+1, aux);
				
				aux = GoluriPrimiteFinal.get(i);
				GoluriPrimiteFinal.set(i, GoluriPrimiteFinal.get(i+1));
				GoluriPrimiteFinal.set(i+1, aux);
				
			}
			
		}
		
		for( int i = 0; i < n; i++ )
		System.out.println(Echipa + " " + PuncteEchipaFinal + " " + GoluriDateFinal + " " + GoluriPrimiteFinal);

	}

}

