#ifndef LIST_H
#define LIST_H

#include "listexception.h"
#include "node.h"

template<typename T>
class List{
    Node<T> *first;
    Node<T> *last;
    void create();
    void copy(List<T> &source);
    Node<T>* find(T &what);
    void swap(Node<T> *a, Node<T> *b); //dodo
    Node<T> *findMin(Node<T> *from); //dodo
public:
    List(){create();}
    List(List<T> &other);
    ~List();
    void sort(); //primy vyber, dodo
    bool isEmpty() const {return first->next == last;}
    void pushBack(T &d) throw(ListException);
    void pushFront(T &d) throw(ListException);
    T getLast() const {return last->prev->getData();}
    T getFirst() const {return first->next->getData();}
    void popBack() throw(ListException);
    void popFront();
    void clean();
    void print() const noexcept;//throw();
    bool remove(T &what);
    T& operator[](unsigned index) throw(ListException);
};

template<typename T>
void List<T>::create(){
    first = new Node<T>();
    last = new Node<T>();
    first->next = last;
    last->prev = first;
}

template<typename T>
void List<T>::copy(List<T> &source){
    Node<T> *pointer = source.first->next;
    while(pointer!=source.last){
        this->pushBack(pointer->getData());
        pointer = pointer->next;
    }
}

template<typename T>
List<T>::List(List<T> &other){
    create();
    copy(other);
}

template<typename T>
void List<T>::pushBack(T &d) throw(ListException){
    last->next = new(nothrow) Node<T>(); //novy prvek zapoj za posledni
    if(!last->next)
        throw ListException("Failed to allocate memory");
    last->setData(d); //uloz data do posledniho prvku
    last->next->prev = last; //pred novy prvek zapoj posledni
    last = last->next; //posun ukazatel na posledni na novy
}

//last <-> first
//next <-> prev

template<typename T>
void List<T>::pushFront(T &d) throw(ListException){
    try{
        first->prev = new Node<T>();
        first->setData(d);
        first->prev->next = first;
        first = first->prev;
    }
    catch(bad_alloc &e){
        throw ListException("Failed to allocate memory");
    }
}

template<typename T>
void List<T>::popBack() throw(ListException){
    if(isEmpty())
        throw ListException("Cannot remove from empty list");
    last = last->prev;
    delete last->next;
    last->next = nullptr;
}

template<typename T>
void List<T>::popFront(){
    first = first->next;
    delete first->prev;
    first->prev = nullptr;
}

template<typename T>
void List<T>::clean(){
    while(!isEmpty())
        popFront();
}

template<typename T>
List<T>::~List(){
    clean();
    delete first;
    delete last;
}

template<typename T>
void List<T>::print() const noexcept{
    Node<T> *pointer = first->next;
    while(pointer!=last){
        pointer->print();
        pointer = pointer->next;
    }
}

template<typename T>
Node<T>* List<T>::find(T &what){
  last->setData(what);
  Node<T> *pointer = first->next;
  for(;pointer->getData()!=what;pointer=pointer->next);
  return pointer == last ? nullptr : pointer;
}

template<typename T>
bool List<T>::remove(T &what){
    Node<T> *p = find(what);
    if(!p)
        return false;
    p->next->prev = p->prev;
    p->prev->next = p->next;
    delete p;
    return true;
}

template<typename T>
Node<T>* List<T>::findMin(Node<T> *from){
    Node<T> *min = from;
    while(from!=last){
        if(from->data < min->data)
            min = from;
        from = from->next;
    }
    return min;
}

template<typename T>
void List<T>::swap(Node<T> *a, Node<T> *b){
    T c = a->data;
    a->data = b->data;
    b->data = c;
}

template<typename T>
void List<T>::sort(){
    if(isEmpty())
        return;
    Node<T> *p = first->next;
    Node<T> *min;
    while(p!=last){
        min = findMin(p);
        if(p!=min)
            swap(p, min);
        p = p->next;
    }
}

template<typename T>
T& List<T>::operator [](unsigned index)
                    throw(ListException)
{
    if(isEmpty())
        throw ListException("Cannot index empty list!");
    Node<T> *p = first->next;
    for(unsigned i = 0; i < index; i++){
        p = p->next;
        if(p == last)
            throw ListException("Index out range!");
    }
    return p->data;
}

#endif // LIST_H
