#include <iostream>
#include <string>

void wyjat(const int a)
{
	if (a == 5)
	{
		std::string wyjatek = "tak nie wolno";
		throw wyjatek;
	}
	std::cout << "a tak wolno" << std::endl;
}

void main()
{
	try
	{
		wyjat(5);
	}
	catch (std::string s)
	{

		std::cout << "Wyjątek:   " << s << std::endl;
	
	}
}

#ifndef KEYED_QUEUE_H
#define KEYED_QUEUE_H

#include <map>
#include <memory>
//#include "lookup_exception.h"

template <class K, class V>
class keyed_queue {
public:
  keyed_queue() :
    map_ptr(std::make_shared<map_t>(map_t())),
    first_node(nullptr),
    last_node(nullptr) {
  }; // TODO: defaultowy destruktor zostawia 1 reachable blok pamięci
    
  keyed_queue(keyed_queue const &);
  keyed_queue(keyed_queue &&);
  keyed_queue &operator=(keyed_queue other);
  void push(K const &k, V const &v);
  void pop();
  void pop(K const &);
  void move_to_back(K const &k);
  std::pair<K const &, V &> front();
  std::pair<K const &, V &> back();
  std::pair<K const &, V const &> front() const;
  std::pair<K const &, V const &> back() const;
  std::pair<K const &, V &> first(K const &key);
  std::pair<K const &, V &> last(K const &key);
  std::pair<K const &, V const &> first(K const &key) const;
  std::pair<K const &, V const &> last(K const &key) const;
  size_t size() const;
  bool empty() const;
  void clear();
  size_t count(K const &) const;
  /* - Iterator k_iterator oraz metody k_begin i k_end, pozwalające przeglądać
  zbiór kluczy w rosnącej kolejności wartości. Iteratory mogą być unieważnione
  przez dowolną operację modyfikacji kolejki zakończoną powodzeniem
  (w tym front, back, first i last w wersjach bez const).
  Iterator powinien udostępniać przynajmniej następujące operacje:
  - konstruktor bezparametrowy i kopiujący
  - operator++ prefiksowy
  - operator== i operator!=
  - operator*
  */

private:
struct Node {
  std::unique_ptr<Node> prev;
  std::unique_ptr<Node> next;
  V v;
};

std::shared_ptr<std::map<K, Node>> map_ptr;
std::shared_ptr<Node> first_node;
std::shared_ptr<Node> last_node;

using map_t = std::map<K, Node>;

//~keyed_queue();
};

#endif /* KEYED_QUEUE_H */
