#include <algorithm>
#include <vector>
#include <iostream>
using namespace std;

int n, q, leftBound, rightBound, from, to;
int *values;
vector<int> *tree;

int search(int vertex, int currentLeft, int currentRight) {
	if (leftBound <= currentLeft && rightBound >= currentRight) {
		return upper_bound(tree[vertex].begin(), tree[vertex].end(), to) 
		- lower_bound(tree[vertex].begin(), tree[vertex].end(), from);
	}


	if (currentRight < leftBound || currentLeft > rightBound) return 0;


	int mean = (currentLeft + currentRight) / 2;
	return search(vertex * 2, currentLeft, mean) + search(vertex * 2 + 1, mean + 1, currentRight);
}

void build(int vertex, int currentLeft, int currentRight) {
	if (currentLeft == currentRight) {
		tree[vertex].push_back(values[currentLeft]);
	}
	else {
		int mean = (currentLeft + currentRight) / 2;
		build(vertex * 2, currentLeft, mean);
		build(vertex * 2 + 1, mean + 1, currentRight);

		merge(
			tree[vertex * 2].begin(), 
			tree[vertex * 2].end(),
			tree[vertex * 2 + 1].begin(), 
			tree[vertex * 2 + 1].end(), 
			back_inserter(tree[vertex])
			);
	}
}

void init() {
	scanf("%d%d", &n, &q);
	values = new int[n];
	tree = new vector<int>[n*4];
	for (int i = 0; i < n; i++) {
		scanf("%d", &values[i]);
	}
	build(1, 0, n - 1);
}

void readBounds() {
	scanf("%d%d%d%d", &leftBound, &rightBound, &from, &to);
	leftBound--;
	rightBound--;
}

void search() {
	readBounds();
	printf("%d\n", search(1, 0, n - 1));
}

int main() {
	init();
	for (int i = 0; i < q; i++) search();
}

