// mongoimport --db tempTestDb --collection restaurants --drop --file ~/Desktop/primer-dataset.json

// mongo

// show dbs              		(shows all databases)

// use tempTestDb          	(chooses specific database to use)


// (shows collections in the current database(showed ["restaurants"]), its how i knew to use db.restaurants.find() below.

db.getCollectionNames()	



// (this finds all restaurants, no filters or anything, so it shows a massive amount of data)
db.restaurants.find()	



// (Finds the first 10 restaurants and sorts by name(alphabetical) and limits the results to 10
db.restaurants.find()		 
	.sort({name:1})
	.limit(10);



//(Finds restaurant by id. You must know the ID beforehand...obviously)
db.restaurants.findOne({				              	
	_id: ObjectId("59074c7c057aaffaafb0e20b")	
})


//(WHAT THE HELL IS THIS?)
var myId = db.restaurants.findOne({}, {_id: true})._id;		  
db.restaurants.findOne({_id: myId});





// (Uses a query object to filter the selection, and finds only those with {"borough": "Queens"}   )
db.restaurants.find({		         
	"borough": "Queens"
})


//(Finds the number of documents in db.restaurants)

db.restaurants.count()	        


//(Counts by nested value, needed to use .notation for address.zipcode)

db.restaurants.find({"address.zipcode": "11206"}).count()        


//deletes by id

db.restaurants.deleteOne({
	_id: ObjectId("59074c7c057aaffaafb11fbf")
})


// (WHAT THE HELL IS THIS?)

var myId = db.restaurants.findOne({}, {_id: true})._id;		     
db.restaurants.removeOne({_id: myId});




//(Uses the ObjectId query selector and uses the update operator $set to change the name.
//Other operators are $unset and $rename

db.restaurants.updateOne({				                      									
	_id: ObjectId("59074c7c057aaffaafb0da82")
},{
	$set: {name: 'Bizz Bar Bang'}
});


//(Uses the address.zipcode query selector to select all objects with zipcode 10035, 						
//and uses updateMany to set all the zipcodes to 10036.)
db.restaurants.updateMany({					                
	"address.zipcode" : "10035"
},{
	$set: {"address.zipcode": "10036"}
});

namespace Gurux.DLMS.Enums
{
    /// <summary>
    ///  Enumerated DLMS error codes.
    /// </summary>
    public enum ErrorCode
    {
        /// <summary>
        /// Disconnect Mode.
        /// </summary>
        DisconnectMode = -4,
        /// <summary>
        /// Receive Not Ready.
        /// </summary>
        ReceiveNotReady = -3,
        /// <summary>
        /// Connection is rejected.
        /// </summary>
        Rejected = -2,
        /// <summary>
        /// Unacceptable frame.
        /// </summary>
        UnacceptableFrame = -1,
        /// <summary>
        /// No error has occurred.
        /// </summary>
        Ok = 0,
        /// <summary>
        /// Access Error : Device reports a hardware fault.
        /// </summary>
        HardwareFault = 1,

        /// <summary>
        /// Access Error : Device reports a temporary failure.
        /// </summary>
        TemporaryFailure = 2,

        /// <summary>
        /// Access Error : Device reports Read-Write denied.
        /// </summary>
        ReadWriteDenied = 3,

        /// <summary>
        /// Access Error : Device reports a undefined object
        /// </summary>
        UndefinedObject = 4,

        /// <summary>
        /// Access Error : Device reports a inconsistent Class or object
        /// </summary>
        InconsistentClass = 9,

        /// <summary>
        /// Access Error : Device reports a unavailable object
        /// </summary>
        UnavailableObject = 11,

        /// <summary>
        /// Access Error : Device reports a unmatched type
        /// </summary>
        UnmatchedType = 12,

        /// <summary>
        /// Access Error : Device reports scope of access violated
        /// </summary>
        AccessViolated = 13,

        /// <summary>
        /// Access Error : Data Block Unavailable.
        /// </summary>
        DataBlockUnavailable = 14,

        /// <summary>
        /// Access Error : Long Get Or Read Aborted.
        /// </summary>
        LongGetOrReadAborted = 15,

        /// <summary>
        /// Access Error : No Long Get Or Read In Progress.
        /// </summary>
        NoLongGetOrReadInProgress = 16,

        /// <summary>
        /// Access Error : Long Set Or Write Aborted.
        /// </summary>
        LongSetOrWriteAborted = 17,

        /// <summary>
        /// Access Error : No Long Set Or Write In Progress.
        /// </summary>
        NoLongSetOrWriteInProgress = 18,

        /// <summary>
        /// Access Error : Data Block Number Invalid.
        /// </summary>
        DataBlockNumberInvalid = 19,

        /// <summary>
        /// Access Error : Other Reason.
        /// </summary>
        OtherReason = 250
    }
}
