#include <SoftwareSerial.h>
#include <DFPlayer_Mini_Mp3.h>

const int amplifier_pin = 2;
const int just_5v_pin = 9;
const int door_pin = 8;
const int finish_quest_pin = 12;
int finish_quest_pin_count = 0;
const int finish_quest_pin_need_count = 5;
int door_pin_active_count = 0;
const int door_pin_need_count = 10;
bool first_track_played = false;
bool second_track_played = false;

void setup()
{
  Serial.begin (9600);
  mp3_set_serial(Serial);
  delay(100);
  mp3_set_volume (23);
    
  pinMode(amplifier_pin, OUTPUT); //включаем усилитель
  pinMode(just_5v_pin, OUTPUT); //просто даем напряжение
  pinMode(door_pin, INPUT); //ждем сигнала с двери
  pinMode(finish_quest_pin, INPUT); //ждем сигнала от розы ветров 

  digitalWrite(amplifier_pin, HIGH);
  digitalWrite(just_5v_pin, HIGH);
  delay(3000);
  digitalWrite(amplifier_pin, LOW);
  delay(500);
  digitalWrite(amplifier_pin, HIGH);
  delay(5000);
}

void loop()
{
	if ( !first_track_played && digitalRead(door_pin) == HIGH )
	{
		if ( door_pin_active_count < door_pin_need_count )
		{
			door_pin_active_count++;
			delay(10);
		}
		else if ( door_pin_active_count >= door_pin_need_count )
		{
			delay(10);
			mp3_play(1);
			delay(100);
			first_track_played = true;
			door_pin_active_count = 0;
			delay(10);
		}
	}
	else if( !second_track_played && digitalRead(finish_quest_pin) == HIGH )
	{
		if ( finish_quest_pin_count < finish_quest_pin_need_count )
		{
			finish_quest_pin_count++;
		}
		else
		{
			mp3_play(2);
			delay(100);
			second_track_played = true;
		}

	}


}

