function getDates(year, month, dow) {
  var d = new Date(0);
  d.setFullYear(year);
  d.setMonth(month);
  
  var day = dow - d.getDay() + 1;
  if (day <= 0) {
    day += 7;
  }
  
  var days = [];
  while (d.getMonth() == month) {
    days.push(day);
    day += 7;
    d.setDate(day);
  }
  return days;
}

