char *  itoa ( int value, char * str )
{
 
    int i =0;
    while (value > 0) {
        int digito = value % 10;
 
        str[i] = digito + '0';
        value /= 10;
        i++;
 
    }
    int ic=0;
    for(int a =i-1; a>=0; a--){
        str[ic] = str[a];
        ic++;
    }
    return str;
 
 
}
 
