const loop = (sequence, curr = 0) => {
    const type = typeof sequence[curr]
    if (type === 'function') {
      sequence[curr]()
      loop(sequence, curr + 1)
    } else if (type === 'number') {
      setTimeout(() => {
        loop(sequence, curr + 1)
      }, sequence[curr])
    }
  }
