class Zad2 {
  def lpowiel(stream: Stream[Int]):Stream[Int] = {
    def insideFunc(actualNum: Int, stream: Stream[Int]): Stream[Int] ={
      (actualNum,stream) match {
        case (_,Stream.Empty) => Stream.Empty
        case (-1, head#::tail) =>
          if (head < 1) insideFunc(-1, tail)
          else insideFunc(head, stream)
        case (0, _#::tail) => insideFunc(-1, tail)
        case(n, head#::tail) => head#::insideFunc(n-1, stream)
      }
    }
    insideFunc(-1,stream)
  }

  def ltake(n: Int, stream: Stream[Int]):List[Int] = {
    (n, stream) match {
      case(0, _) => Nil
      case(_, Stream.Empty) => Nil
      case(n, hd#::tail) => hd::ltake(n - 1, tail)
    }
  }
}

object Zad2 {
  def main(args: Array[String]): Unit = {
    val test = new Zad2()
    System.out.println(test.ltake(100, test.lpowiel(Stream.cons(1,Stream.cons(2,Stream.cons(3,Stream.cons(-5,Stream.cons(-1,Stream.cons(7, Stream.Empty)))))))))
    System.out.println(test.ltake(100, test.lpowiel(Stream.cons(0,Stream.cons(9,Stream.cons(6,Stream.cons(4,Stream.cons(-1,Stream.cons(3, Stream.Empty)))))))))
    System.out.println(test.ltake(100, test.lpowiel(Stream.Empty)))
  }
}
