package fr.clavat.test;

public class QuickSortSimpleVersion {
	private long[] data;
	private String[] nom;

	  private int len;

	  public QuickSortSimpleVersion(int max) {
	    data = new long[max];
	    nom = new String[max];
	    len = 0;
	  }

	  public void insert(long data, String nom) {
	    this.data[len] = data; 
	    this.nom[len] = nom;
	    len++;
	  }

	  public String display() {
		  String res = "";
	    for (int j = 0; j < len; j++)
	      res += "\n"+j+". "+ nom[j] + " - " + data[j];
	    return res;
	  }

	  public void quickSort() {
	    recQuickSort(0, len - 1);
	  }

	  public void recQuickSort(int left, int right) {
	    if (right - left <= 0) // if size <= 1 already sorted
	      return;
	    else // size is 2 or larger
	    {
	      long pivot = data[right]; // rightmost item
	      // partition range
	      int partition = partitionData(left, right, pivot);
	      recQuickSort(left, partition - 1); // sort left side
	      recQuickSort(partition + 1, right); // sort right side
	    }
	  }

	  public int partitionData(int left, int right, long pivot) {
	    int leftPtr = left - 1; // left (after ++)
	    int rightPtr = right; // right-1 (after --)
	    while (true) { // find bigger item
	      while (data[++leftPtr] < pivot)
	        ;
	      // find smaller item
	      while (rightPtr > 0 && data[--rightPtr] > pivot)
	        ;

	      if (leftPtr >= rightPtr) // if pointers cross, partition done
	        break;
	      else
	        swap(leftPtr, rightPtr);
	    }
	    swap(leftPtr, right); // restore pivot and return pivot location
	    return leftPtr;
	  }

	  public void swap(int d1, int d2) {
	    long temp = data[d1];
	    data[d1] = data[d2];
	    data[d2] = temp;
	    String tempo = nom[d1]; //on echange egalement les noms
	    nom[d1] = nom[d2];
	    nom[d2] = tempo;
	  }

	  public static void main(String[] args) {
	    int maxSize = 16; // array size
	    QuickSortSimpleVersion arr = new QuickSortSimpleVersion(maxSize); // create array

	    for (int j = 0; j < maxSize; j++) // fill array with random numbers
	    {
	      long n = (int) (java.lang.Math.random() * 99);
	      arr.insert(n,"test: "+n);
	    }
	    arr.display();
	    arr.quickSort();
	    arr.display();
	  }

	}

# DuelArena will start automatically with 3 players 

import minqlx
import time
import math

MAX_ACTIVE_PLAYERS = 4 # max players for duelarena
MIN_ACTIVE_PLAYERS = 3 # min players for duelarena

class duelarena(minqlx.Plugin):

    def __init__(self):

        self.add_hook("team_switch_attempt", self.handle_team_switch_event)
        self.add_hook("player_disconnect", self.handle_player_disco)    
        self.add_hook("player_loaded", self.handle_player_loaded, priority=minqlx.PRI_LOWEST) 
        self.add_hook("round_countdown", self.handle_round_countdown)
        self.add_hook("game_countdown",self.handle_game_countdown)
        self.add_hook("round_end", self.handle_round_end)
        self.add_hook("game_end", self.handle_game_end)
        self.add_hook("map", self.handle_map_change)
        self.add_command("duelarena", self.cmd_duelarena, 5, usage="[auto|force]")
        self.add_command(("duel", "d"), self.cmd_duel)
        self.add_command("join", self.cmd_join)    

        self.forceduel = False           #False: Start Duelarena automatically, True: Force Duelarena 
        self.duelmode = False            #global gametype switch
        self.initduel = False            #initial player setup switch
        self.playerset = []              #collect players who joined a team
        self.queue = []                  #queue for rotating players
        self.player_red = None           #force spec exception for this player
        self.player_blue = None          #force spec exception for this player
        self.player_spec = []            #force spec exception for this players
        self.scores = {}                 #store/restore individual team scores
        self.duelvotes = []              #!d !duel votes counter
        self.print_reset_scores = False  #flag for round_countdown
        self.player_agree_to_join = None #!join command
        
        #initialize playerset on plugin reload        
        teams = self.teams()        
        for _p in teams['red'] + teams['blue']:
            if _p.steam_id not in self.playerset: self.playerset.append(_p.steam_id)

    def handle_map_change(self, mapname, factory):
        self.forceduel = False
        self.duelvotes = []
        self.duelmode = False
        self.initduel = False
        
    # Don't allow players to join manually when DuelArena is active
    def handle_team_switch_event(self, player, old, new):
    
        if not self.game: return
    
        if new in ['red', 'blue', 'any'] and player.steam_id not in self.playerset: 
            self.playerset.append(player.steam_id) # player joined a team? Add him to playerset
            self.duelarena_switch(player) # we good enough for DuelArena?
        elif new in ['spectator'] and player.steam_id in self.playerset: # oh, player left team? let's see what we do with him...
            if player.steam_id not in self.player_spec: # player initiated switch to spec? Remove him from playerset
                self.playerset.remove(player.steam_id)
                if player.steam_id in self.queue: self.queue.remove(player.steam_id)
                self.duelarena_switch(player) 
            elif player.steam_id in self.player_spec: # we initiated switch to spec? Only remove him from exception list
                self.player_spec.remove(player.steam_id)

        if self.game.state == "warmup" and len(self.playerset) == 3:
            self.center_print("Ready up for ^6DuelArena^7!")
            self.msg("Ready up for ^6DuelArena^7!")
            return        
        elif self.game.state == "warmup": return
        
        if not self.duelmode: return
        
        # If we initiated this switch, allow it
        if player == self.player_red or player == self.player_blue:
            self.player_red = None
            self.player_blue = None
            return
            
        # If they wanted to join a team, halt this hook at enginge-level and other hooks from being called
        if new in ['red', 'blue', 'any']:
            player.tell("Server is now in ^6DuelArena^7 mode. You will automatically rotate with round loser.")
            return minqlx.RET_STOP_ALL
    
    # Announce next duel    
    def handle_round_countdown(self, round_number):
    
        if self.duelmode:
            teams = self.teams()
            if teams["red"] and teams["blue"]:
                self.center_print("{} ^2vs^7 {}".format(teams["red"][-1].name, teams["blue"][-1].name))
                self.msg("DuelArena: {} ^2vs^7 {}".format(teams["red"][-1].name, teams["blue"][-1].name))
                
        if self.print_reset_scores:
            self.print_results()
            self.reset_team_scores()
            self.print_reset_scores = False
                                    
    # check if we need to deavtivate DuelArena on player disconnect        
    @minqlx.delay(3)
    def handle_player_disco(self, player, reason):
        if player.steam_id in self.playerset:
            self.playerset.remove(player.steam_id)
            self.duelarena_switch()
        if player.steam_id in self.duelvotes:
            self.duelvotes.remove(player.steam_id)      
         
    @minqlx.delay(3)
    def handle_player_loaded(self, player):
        connected_players = self.connected_players()
        if self.game.state == "in_progress":
            if self.forceduel and not self.check_duel_abort(1):
                player.tell("{}, DuelArena match is in progress. Join to enter DuelArena! Round winner stays in, loser rotates with spectator.".format(player.name))
            elif self.forceduel and self.check_duel_abort(1):
                player.tell("{}, by joining DuelArena will be aborted and server switches to standard CA!".format(player.name))
            elif player.team == "spectator" and len(self.playerset) == 3:
                player.tell("{}, type !join to join Duel Arena or press join button to force switch to Clan Arena!".format(player.name))
                self.player_agree_to_join = player.steam_id
        elif player.team == "spectator" and len(self.playerset) == 2:
            player.tell("{}, join to activate DuelArena! Round winner stays in, loser rotates with spectator.".format(player.name))
        elif connected_players >= MIN_ACTIVE_PLAYERS and connected_players <= MAX_ACTIVE_PLAYERS:
            player.tell("{}, type ^6!duel^7 or ^6!d^7 to vote for DuelArena! Round winner stays in, loser rotates with spectator. Hit 8 rounds first to win!".format(player.name))

    # When a game is about to start and duelmode is active, initialize
    @minqlx.delay(3)        
    def handle_game_countdown(self):

        self.duelarena_switch()
    
        if self.duelmode:
            self.init_duel()
            
    def handle_game_end(self, data): 
        
        if not self.game: return
    
        #put both players back to the queue, winner first position, loser last position
        if self.duelmode:

            if int(data['TSCORE1']) > int(data['TSCORE0']):
                loser = "red"
                winner = "blue" 
            else: 
                loser = "blue"
                winner = "red" 

            teams = self.teams() 
        
            try:
                self.queue.insert(0, teams[loser][-1].steam_id)
            except:
                pass
            try:          
                self.queue.append(teams[winner][-1].steam_id) 
                self.scores[teams[winner][-1].steam_id] += 1
            except:
                pass
                      
            self.print_results()  

    @minqlx.delay(1.5)
    def handle_round_end(self, data):
    
        # Not in CA? Do nothing
        if (self.game is None) or (self.game.type_short != "ca"): return
        
        # Last round? Do nothing except adding last score point to winner
        if self.game.roundlimit in [self.game.blue_score, self.game.red_score]:
            return
                
        if self.initduel:
            # Check if we can use the winner of the pre-duel round
            winner_sid = None 
            teams =  self.teams()
            if int(len(teams["red"])) == 1 and int(len(teams["blue"])) == 1: # check if we had 1v1
                if data['TEAM_WON'] == 'RED': 
                    winner_sid = teams['red'][-1].steam_id
                elif data['TEAM_WON'] == 'BLUE':  
                    winner_sid = teams['blue'][-1].steam_id      
        
            self.init_duel(winner_sid)
            return
    
        if self.duelmode:
        
            teams =  self.teams()

            if data['TEAM_WON'] == 'RED': 
                empty_team = 'blue' 
                loser_team_score = self.game.blue_score
                winner = teams['red'][-1]
                self.scores[winner.steam_id] = self.game.red_score
            elif data['TEAM_WON'] == 'BLUE':  
                empty_team = 'red' 
                loser_team_score = self.game.red_score
                winner = teams['blue'][-1]
                self.scores[winner.steam_id] = self.game.blue_score
            else: return # Draw? Do nothing           

            try:
                next_player = self.player(self.queue.pop())
            except:
                self.msg("Problem fetching next player. Aboting DuelArena ...")
                self.deactivate_duel()
                return
                            
            if next_player.team != "spectator":
                self.msg("Problem fetching next player. Aboting DuelArena ...")
                self.deactivate_duel()
                return
               
            self.player_blue = next_player
            self.player_red = next_player
            loser = teams[empty_team][-1]
            next_player.put(empty_team)
            self.game.addteamscore(empty_team, self.scores[next_player.steam_id]-loser_team_score) # restore switched player team score
            minqlx.console_command("echo duelarena data_team_won={}, winner={}, loser={}".format(data['TEAM_WON'], winner.name, loser.name))
            self.queue.insert(0, loser.steam_id)
            self.player_spec.append(loser.steam_id)
            self.scores[loser.steam_id] = loser_team_score # store loser team score
            loser.put("spectator")
            loser.tell("{}, you've been put back to DuelArena queue. Prepare for your next duel!".format(loser.name))            
                           
    def init_duel(self, winner_sid=None):
     
        self.checklists()
        self.init_duel_team_scores()  # set all player scores 0
                  
        for sid in self.playerset:
            if sid not in self.queue:
                self.queue.insert(0, sid)
     
        teams = self.teams()
        
        if winner_sid and winner_sid in self.queue:
            self.player_red = self.player(winner_sid)
            self.queue.remove(winner_sid)
        else:
            self.player_red = self.player(self.queue.pop())

        self.player_blue = self.player(self.queue.pop())
        
        #both players already on different teams? Do nothing
        if not ((self.player_blue.team == 'blue' and self.player_red.team == 'red') or (self.player_blue.team == 'red' and self.player_red.team == 'blue')):
            #only one player already in any team?
            if self.player_red.team == 'red': self.player_blue.put("blue")
            elif self.player_red.team == 'blue': self.player_blue.put("red")
            elif self.player_blue.team == 'blue': self.player_red.put("red")
            elif self.player_blue.team == 'red': self.player_red.put("blue")
            #both players not in teams?
            else:
                self.player_red.put("red")
                self.player_blue.put("blue")
    
        # put all other players to spec        
        for _p in teams['red'] + teams['blue']:
            if _p != self.player_red and _p != self.player_blue:
                _p.put("spectator") 
                    
        self.initduel = False
        
    def duelarena_switch(self, player=None):
    
        self.checklists()
        
        if self.check_duel_abort():
            self.deactivate_duel()
            return
                                            
        # admin forced Duelarena?
        if self.forceduel:
            if not self.duelmode and len(self.playerset) >= MIN_ACTIVE_PLAYERS:
                self.duelmode = True
                self.announce_duelarena_activation()
                if self.game.state == "in_progress":
                    self.initduel = True
            elif self.duelmode and len(self.playerset) < MIN_ACTIVE_PLAYERS:
                self.deactivate_duel()
            return
        
        # Main conditions not true? Skip the switch
        if not self.duelmode and len(self.playerset) != MIN_ACTIVE_PLAYERS: return
    
        if self.duelmode:
            if len(self.playerset) != MIN_ACTIVE_PLAYERS:
                self.deactivate_duel() 
        elif not self.duelmode:
            if len(self.playerset) == MIN_ACTIVE_PLAYERS:
                self.duelmode = True
                self.announce_duelarena_activation() 
                if self.game and self.game.state == "in_progress":
                    if player: self.queue.append(player.steam_id) # Player switched into a team and game is already in progress? Give him first queue position!
                    self.initduel = True 
    
        minqlx.console_command("echo duelarena_switch: duelmode={}, len_playerset={}, initduel={}".format(self.duelmode, len(self.playerset), self.initduel))
        
    def checklists(self): 
    
        self.queue[:] = [sid for sid in self.queue if self.player(sid) and self.player(sid).ping < 990] 
        self.playerset[:] = [sid for sid in self.playerset if self.player(sid) and self.player(sid).ping < 990]
        
    def reset_team_scores(self):
        if self.game.state == "in_progress":
            self.game.addteamscore('red', -self.game.red_score)
            self.game.addteamscore('blue', -self.game.blue_score)
            
    def init_duel_team_scores(self):
        self.reset_team_scores()
        self.scores = {}
        for sid in self.playerset:
            self.scores[sid] = 0
            
    def print_results(self):
        self.msg("DuelArena results:")
        place = 0
        prev_score = -1
        for pscore in sorted(self.scores.items(), key=lambda x: x[1], reverse=True):
            if pscore[1] != prev_score: place += 1
            prev_score = pscore[1]
            player = self.player(pscore[0])
            if player: self.msg("Place ^3{}.^7 {} ^7(Wins:^2{}^7)".format(place, player.name, pscore[1]))
            
    def cmd_duelarena(self, player, msg, channel):
    
        if len(msg) < 2 or msg[1] not in ["auto", "force"]:
            state = "auto"
            if self.forceduel: state = "force"
            self.msg("Current DuelArena state is: ^6{}".format(state))
            return minqlx.RET_USAGE
        if msg[1] == "force":
            self.forceduel = True
            self.msg("^7Duelarena is now ^6forced^7! With next map DuelArena will switch to automatic.")
        elif msg[1] == "auto":
            self.forceduel = False
            self.msg("^7Duelarena is now ^6automatic^7!")
        self.duelarena_switch()
        
    def cmd_duel(self, player, msg, channel):
    
        if self.forceduel:
            self.msg("^7DuelArena already active!")
            return
    
        if self.game.state == "warmup":

            connected_players = self.connected_players()
        
            if connected_players > MAX_ACTIVE_PLAYERS or connected_players < MIN_ACTIVE_PLAYERS:
                player.tell("^6!duel^7 votes only available with {}-{} players connected.".format(MIN_ACTIVE_PLAYERS, MAX_ACTIVE_PLAYERS))
                return
                
            if player.steam_id in self.duelvotes:
                return self.msg("^7{} you already voted for DuelArena!".format(player.clean_name))
                 
            self.duelvotes.append(player.steam_id)
            
            have = len(self.duelvotes)
            need = int(math.floor( connected_players / 2)) + 1
            votes_left = need - have
            
            if votes_left <= 0 and have >= 2:
                self.msg("^7Total DuelArena votes = ^6{}^7, vote passed!".format(have))
                self.play_sound("sound/vo/vote_passed.ogg")
                self.forceduel = True
                self.duelarena_switch()
            elif votes_left > 0:
                self.msg("^7Total DuelArena votes = ^6{}^7, but I need ^6{}^7 more to activate DuelArena.".format(have, votes_left))
                
        else:
            self.msg("^7DuelArena votes only allowed in warmup!") 

    def cmd_join(self, player, msg, channel):
    
        if player.steam_id == self.player_agree_to_join:
            self.player_agree_to_join = None
            self.playerset.append(player.steam_id)
            self.queue.insert(0, player.steam_id)
            self.forceduel = True
            player.tell("You successfully joined the DuelArena queue. Prepare for your duel!")
            self.msg("^7{} joined DuelArena!".format(player.clean_name))
        
    def announce_duelarena_activation(self):
        self.msg("DuelArena activated! Round winner stays in, loser rotates with spectator. Hit 8 rounds first to win!") 
        self.center_print("DuelArena activated!")
        
    def connected_players(self):
        teams =  self.teams()
        players = int( len(teams["red"] + teams["blue"] + teams["spectator"]) )
        return players
        
    def check_duel_abort(self, joiner=0):
        if not self.forceduel and not self.duelmode:
            return False
            
        if not self.game or self.game.state != "in_progress":
            return False

        if len(self.playerset) + joiner <= MAX_ACTIVE_PLAYERS:
            return False

        for score in self.scores.values():
            if score >= 6:
                return False

        return True
        
    def deactivate_duel(self):
        self.forceduel = False
        self.duelmode = False
        self.initduel = False
        self.msg("DuelArena has been deactivated!") 
        self.center_print("DuelArena deactivated!") 
        if self.game.state == "in_progress":
            self.print_reset_scores = True

