$shouldDeleteImages = Read-Host -Prompt 'Do you wish to delete all images? Default is no. (y/n)'

$listOfContainers = (docker ps -a -q)

if($listOfContainers)
{
    "There are containers, they shall now be removed."

    docker rm $($listOfContainers) --force
}
else
{
    "There are no containers to remove."
}

$listOfVolumes = (docker volume ls -q)

if($listOfVolumes)
{
    "There are volumes, they shall now be removed."

    docker volume rm $($listOfVolumes) --force
}
else
{
    "There are no volumes to remove."
}

if($shouldDeleteImages -eq "y")
{
    $listOfImages = (docker images -q)

    if($listOfImages)
    {
        "There are images, they shall now be removed."

        docker rmi $($listOfImages) --force
    }
    else
    {
        "There are no images to remove."
    }
}

public static void Log (MessageEntry message)
{
	delegate() {
		foreach (KeyValuePair<Stream, Flags> entry in mOutputStreams) {
			if (message.HasFlags (entry.Value)) {
				delegate() { entry.Key.WriteLine (message); }.BeginInvoke ();
			}
		}
	}.BeginInvoke ();
}

