@Configuration
@EnableWebSecurity
@EnableGlobalMethodSecurity(securedEnabled = true)
public class SecurityConfig extends WebSecurityConfigurerAdapter {

    @Autowired
    @Qualifier("MyAuthenticationProvider")
    private AuthenticationProvider authenticationProvider;

    @Override
    protected void configure(AuthenticationManagerBuilder auth) throws Exception {
        auth.authenticationProvider(authenticationProvider);
    }

    @Override
    protected void configure(HttpSecurity http) throws Exception {
        http
                .csrf()
                .disable()
                .headers()
                .frameOptions().disable()
                .and()
                .authorizeRequests()
                .antMatchers("/", "/favicon.ico", "/resources/**")
                .permitAll()
                .antMatchers("/admin/**")
                .hasAnyRole("ADMIN")
                .antMatchers("/user/**")
                .hasAnyRole("ADMIN", "USER_MANAGER")
                .and()
                .formLogin()
                .loginPage("/")
                .permitAll().loginProcessingUrl("/authenticate").usernameParameter("j_username")
                .passwordParameter("j_password").defaultSuccessUrl("/index").and().logout()
                .logoutUrl("/logout").logoutRequestMatcher(new AntPathRequestMatcher("/logout")).clearAuthentication(true).invalidateHttpSession(true)
                .logoutSuccessUrl("/logoutsuccessfully").permitAll()
                .and().exceptionHandling().accessDeniedPage("/403");
    }

    @Override
    @Bean(name = "authenticationManager")
    public AuthenticationManager authenticationManagerBean() throws Exception {
        return super.authenticationManagerBean();
    }
	
