/*You will be given a string with brackets and an index of an opening bracket and your task will be to return the index of the matching closing bracket. Return -1 if there is no answer. An opening brace will always have a closing brace.*/

function solve(str, indx){
  var bracMatcher = []
  for(var i = 0; i < str.length; i++){
      if(str[i] == '(') bracMatcher.push(i)

      if(str[i] == ')' && bracMatcher.length != 0){
         if(bracMatcher[bracMatcher.length - 1] == indx) return i
         bracMatcher.pop()
      }
  }
  return -1
}

