void multiply(float A[][100], float (&v)[100], int n) {
	int i, j, k, sum = 0;
	float product[100]={0};
	for (i=0;i<n;i++){
        for(j=0;j<n;j++){
            product[i]+=(A[i][j]*v[j]);
        }
    }
    for(i=0;i<n;i++)
    	v[i] = product[i];
}

void normalize(float (&v)[100], int n) {
	float sum = 0;
	int i;
	for(i=0;i<n;i++) {
		sum += v[i];
	}

	for(i=0;i<n;i++) {
		v[i] /= sum;
	}
}

void printMatrix(float A[][100], int n) {
	int i, j;
	for(i=0;i<sqrt(n);i++) {
		for(j=0;j<sqrt(n);j++) {
			cout << A[i][j] << " ";
		}
		cout << endl;
	}
}

void printVector(float v[], int n) {
	int i;
	for(i=0;i<n;i++)
		cout << v[i] << " ";
	cout << endl;
}

float maxValue(float v[], int n) {
	float ans = v[0];
	int i;
	for(i=0;i<n;i++) {
		if(v[i]> ans)
			ans = v[i];
	}
	return ans;
}

int main() {
	int n, i, j, index;
	float A[100][100] = {{0.0}}, precision = 0.000001;
	int count[10];
	float v[100];
	float prev[100] = {0.0};
	float diff[100] = {0.0};
	cin >> n;
	for(i=0;i<sqrt(n);i++) {
		cin >> count[i];
		for(j=0;j<count[i];j++) {
			cin >> index;
			A[index][i] = (float)1/(float)count[i];
		}
	}

	for(i=0;i<sqrt(n);i++)
		v[i] = 0.5;
	i = 0;
	do {
		cout << "Iteration " << i << " ";
		for(j=0;j<sqrt(n);j++)
			prev[j] = v[j];
		multiply(A, v, sqrt(n));
		normalize(v, sqrt(n));
		printVector(v, sqrt(n));
		for(j=0;j<sqrt(n);j++)
			diff[j] = abs(prev[j]-v[j]);
		i++;	
	}
	while(maxValue(diff, sqrt(n)) > precision);
	printVector(v, sqrt(n));
	return 0;
}

