function X = fourierTransform(x, w, t)


if ~iscolumn(x) || ~iscolumn(t) || ~iscolumn(w)
    error('Inputs must be column vector!');
end

if ( length(t) > 1 )
    dt=t(2)-t(1);
else
    dt=t(1);
end
trow=t'; %%get the transpose
phy = exp(-1j*w*trow);
X = dt*(phy*x) ;
end

#include <stdio.h>
#include <stdlib.h>
#include <memory.h>

void* preorder(char* atree, int i, int max)
{
	if(i >= max)
		return NULL;

	printf("%c", atree[i]);
	preorder(atree, i * 2, max);
	preorder(atree, i * 2 + 1, max);
	
	return NULL;
}

void* inorder(char* atree, int i, int max)
{
	if(i >= max)
		return NULL;

	inorder(atree, i * 2, max);
	printf("%c", atree[i]);
	inorder(atree, i * 2 + 1, max);

	return NULL;
}

void* postorder(char* atree, int i, int max)
{
	if(i >= max)
		return NULL;

	postorder(atree, i * 2, max);
	postorder(atree, i * 2 + 1, max);
	printf("%c", atree[i]);

	return NULL;
}

void main(int argc, char** argv)
{
	char ArrayTree[15] = {0,'A','3','*','2','B','4','-','1','C','6','/','5','D','7'};

	printf("preorder :");
	printf("\n");
	preorder(ArrayTree, 1, sizeof(ArrayTree) / sizeof(char));
	printf("\n");

	printf("inorder :");
	printf("\n");
	inorder(ArrayTree, 1, sizeof(ArrayTree) / sizeof(char));
	printf("\n");

	printf("postorder:");
	printf("\n");
	postorder(ArrayTree, 1, sizeof(ArrayTree) / sizeof(char));
	printf("\n");

	getchar();
}
