using System;
using System.Collections.Generic;
using System.Linq;

namespace Test
{
    class Program
    {
        static void Main(string[] args)
        {
            List<Consumer> consumers = new List<Consumer>()
            {
                { new Consumer() { CoachId = 5, UnreadMessagesCount = 5 } },
                { new Consumer() { CoachId = 1, UnreadMessagesCount = 2 } },
                { new Consumer() { CoachId = -1, UnreadMessagesCount = 4 } },
                { new Consumer() { CoachId = 8, UnreadMessagesCount = 0 } },
                { new Consumer() { CoachId = 9, UnreadMessagesCount = 0 } },
                { new Consumer() { CoachId = 7, UnreadMessagesCount = 0 } },
                { new Consumer() { CoachId = -1, UnreadMessagesCount = 10 } },
                { new Consumer() { CoachId = 4, UnreadMessagesCount = 1 } },
                { new Consumer() { CoachId = -1, UnreadMessagesCount = 2 } }
            };

            var query = consumers
                .OrderBy(x => x.CoachId == -1 ? 0 : 1)
                .ThenByDescending(x => x.UnreadMessagesCount)
                .ToList();

            foreach (var item in query)
            {
                Console.WriteLine(item.CoachId + " " + item.UnreadMessagesCount);
            }
        }

        class Consumer
        {
            public int CoachId { get; set; }

            public int UnreadMessagesCount { get; set; }
        }
    }
}
