// The number of values we want (the more we have, the more precise the answer)
#define N 50

using namespace std;

// My function for g(x)
double g(double x) {
	// My provided values for the question
	int a = (-4), k = 9, x0 = 6.0; 
	// The Right Hand Side to the equation
	// d^2y/dx^2 = a*exp(-k(x-x0)^2)
	return (a)* exp(-1 * k * (x - x0) * (x - x0));
}

// This code for Gaussian Elimination was provided by Alasdair to make it easier to do the method
// than the initial code I was using which required me to make an augmented matricies, which can be 
// looked at on request
void gauss_elim(double A[N][N], double B[N]) {
	unsigned j = 0; // non-zero col count
	unsigned i;
	unsigned other_cols, other_rows;
	double coeff;

	for (i = 0; i < N; ++i) { // active column
		coeff = A[j][i];
		for (other_cols = 0; other_cols < N; other_cols++) {
			if (other_cols >= i) {
				A[j][other_cols] /= coeff;
			}
		}
		B[j] /= coeff;
		for (other_rows = 0; other_rows < N; other_rows++) {
			if (other_rows == j) {
				continue;
			}
			coeff = A[other_rows][i];
			for (other_cols = 0; other_cols < N; other_cols++) {
				if (other_cols >= i) {
					A[other_rows][other_cols] -= coeff * A[j][other_cols];
				}
			}
			B[other_rows] -= coeff * B[j];
		}
		++j;
	}
}

int main()
{
	// My interval [LB,UB] and the yL and yR values that were provided
	int yL = 1, yR = 1, LB = 5 /*Lower Bound*/, UB = 7 /*Upper Bound*/;
	// My change in x 
	double dx = (UB - LB) / (N - 1.0);

	// Initialising my D Matrix 
	double D[N][N];
	for (int j = 0; j < N; j++) {
		for (int i = 0; i < N; i++) {
			D[i][j] = 0.0;
		}
	}

	// Setting certain points in my Matrix to perticular numbers
	for (int i = 1; i < (N - 1); i++) {
		D[i][i] = -2.0;
		D[i][i + 1] = 1.0;
		D[i][i - 1] = 1.0;
	}

	// Setting the top left and bottom right of the D Matrix to the 1.0
	D[0][0] = 1.0;
	D[N - 1][N - 1] = 1.0;

	// My D Matrix
	cout << "D:\n";
	for (int j = 0; j < N; j++) {
		for (int i = 0; i < N; i++) {
			if (i) putchar(',');
			printf(" % 9.5lf", D[j][i]);
		
		}
		cout << endl;
	}

	// Setting my top and bottom values of the vector to yL and yR respectively
	double G[N];
	G[0] = yL;
	G[N - 1] = yR;

	// Setting up my X Vector up (along with my G Vector) so i can see the x values for use later on in the code
	double X[N];
	X[0] = LB;
	X[N - 1] = UB;
	for (int i = 1; i < (N - 1); i++) {
		X[i] = LB + (i * dx);
		G[i] = g(X[i]) * ((dx) * (dx));
	}

	// My G vector (g(x)*(dx)^2) with yL and yR the first and last value respectively
	cout << "\nG:\n";
	for (int i = 0; i < N; i++) {
		printf(" % 9.5lf\n", G[i]);
	}

	cout << "\n"; 

	// Gaussian elimination
	gauss_elim(D, G);
	 
	// Printing X and Y in 2 columns so that i can see which values correspond (ready for use on e.g. Excel to plot)
	cout << "|         X |         Y |\n";
	cout << "|-----------+-----------|\n";
	for (int i = 0; i < N; i++) {
		printf("| % 9.5lf | % 9.5lf |\n", X[i], G[i]);
	}
	cout << "\n";

	return 0;
}

